implementation.body:
import groovy.transform.CompileStatic
import org.apache.syncope.core.persistence.api.dao.NotFoundException;
import org.apache.syncope.core.persistence.api.dao.PlainAttrDAO;
import org.apache.syncope.core.persistence.api.dao.PlainSchemaDAO;
import org.apache.syncope.core.persistence.api.dao.UserDAO;
import org.apache.syncope.core.persistence.api.entity.EntityFactory;
import org.apache.syncope.core.persistence.api.entity.PlainSchema;
import org.apache.syncope.core.persistence.api.entity.task.PropagationTask;
import org.apache.syncope.core.persistence.api.entity.task.TaskExec;
import org.apache.syncope.core.persistence.api.entity.user.UPlainAttr;
import org.apache.syncope.core.persistence.api.entity.user.UPlainAttrUniqueValue;
import org.apache.syncope.core.persistence.api.entity.user.UPlainAttrValue;
import org.apache.syncope.core.persistence.api.entity.user.User;
import org.apache.syncope.core.provisioning.api.propagation.PropagationActions;
import org.identityconnectors.framework.common.objects.Attribute;
import org.identityconnectors.framework.common.objects.ConnectorObject;
import org.identityconnectors.framework.common.objects.ObjectClass;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;

@CompileStatic
class MyPropagationActions implements PropagationActions {
    private static final Logger LOGGER = LoggerFactory.getLogger(MyPropagationActions.class);
  
     private static final String attr_gitlab_uid = "gitlab_uid";
    private static final String attr_gitlab_access_token = "gitlab_access_token";

    @Autowired
    protected UserDAO userDAO;

    @Autowired
    protected PlainSchemaDAO plainSchemaDAO;

    @Autowired
    protected PlainAttrDAO plainAttrDAO;

    @Autowired
    private EntityFactory entityFactory;

    @Override
    public void after(PropagationTask task, TaskExec execution, ConnectorObject afterObj) {
        // Only process USER propagations
        if (afterObj != null && ObjectClass.ACCOUNT_NAME.equals(afterObj.getObjectClass().getObjectClassValue())) {
            Attribute id = afterObj.getAttributeByName("id");
            Attribute access_token = afterObj.getAttributeByName("access_token");
            Attribute email = afterObj.getAttributeByName("email");

            if (email != null) {
                String emailValue = email.getValue().get(0).toString();
                User user = userDAO.findByUsername(emailValue);

                if (id != null) {
                    String idValue = id.getValue().get(0).toString();

                    PlainSchema gitlabIdSchema = plainSchemaDAO.find(attr_gitlab_uid);
                    if (gitlabIdSchema == null) {
                        throw new NotFoundException(attr_gitlab_uid + " attribute not found, did you set Syncope up correctly!?");
                    }

                    // If the GitLab ID hasn't been created yet, make it so!
                    if (!user.getPlainAttr(attr_gitlab_uid).isPresent()) {
                        UPlainAttr uPlainAttr = entityFactory.newEntity(UPlainAttr.class);
                        uPlainAttr.setOwner(user);
                        uPlainAttr.setSchema(gitlabIdSchema);

                        UPlainAttrUniqueValue uPlainAttrUniqueValue = entityFactory.newEntity(UPlainAttrUniqueValue.class);
                        uPlainAttrUniqueValue.setStringValue(idValue);
                        uPlainAttrUniqueValue.setSchema(gitlabIdSchema);
                        uPlainAttrUniqueValue.setAttr(uPlainAttr);

                        uPlainAttr.setUniqueValue(uPlainAttrUniqueValue);
                        user.add(uPlainAttr);
                        userDAO.save(user);
                    }
                }
              try {
                LOGGER.error("GOING HERE");
                LOGGER.error(afterObj.toString());
                if (access_token != null) {
                    String accessTokenValue = access_token.getValue().get(0).toString();

                    PlainSchema accessTokenSchema = plainSchemaDAO.find(attr_gitlab_access_token);
                    if (accessTokenSchema == null) {
                        throw new NotFoundException(attr_gitlab_access_token + " attribute not found, did you set Syncope up correctly!?");
                    }
 LOGGER.error("GOING HERE2");
                    if (!user.getPlainAttr(attr_gitlab_access_token).isPresent()) {
                        UPlainAttr uPlainAttr = entityFactory.newEntity(UPlainAttr.class);
                        uPlainAttr.setOwner(user);
                        uPlainAttr.setSchema(accessTokenSchema);
                       LOGGER.error("OISJDF");

                        UPlainAttrValue uplainAttrValue = entityFactory.newEntity(UPlainAttrValue.class);
                        uplainAttrValue.setStringValue(accessTokenValue);
                        uplainAttrValue.setAttr(uPlainAttr);

                        uPlainAttr.add(accessTokenValue, uplainAttrValue);
                        user.add(uPlainAttr);
                        userDAO.save(user);
                    }
                }
              } catch(Exception e) {
                LOGGER.error("HOHO", e);
              }
            }
        }
    }
}
